import React from "react";
import {useState} from "react";
import {
    FormControl,
    FormLabel,
    Input,
    FormHelperText,
  } from "@chakra-ui/react";
import { Box, Center, Container, Button, Image } from "@chakra-ui/react"
function AddSnapshot(props) {

    const [image, setImage] = useState(null)

    const pasteFromClipboard = (e) => {
      if(e.clipboardData.files.length > 0){
        if(e.clipboardData.files[0].type.startsWith("image/")){
          setSnapshotImage(e.clipboardData.files[0]);
        }
      }
    }

    const setSnapshotImage = (file) => {
      const fileReader = new FileReader();

      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        setImage(fileReader.result);
      }

    }
    return (
        <Box maxW="sm" borderWidth="1px" borderRadius="lg" overflow="hidden" mt>
            <Container>
            <Center>
            <Image src="gibbresh.png" fallbackSrc="https://via.placeholder.com/400" />
            </Center>
        <FormControl id="image">
      <Input type="file" />
      <FormHelperText>Ctrl + V to paste your image</FormHelperText>
    </FormControl>
    <Button
            mt={4}
            colorScheme="teal"
            isLoading={props.isSubmitting}
            type="submit"
          >
            Submit
          </Button>
    </Container>
        </Box>
)
}

export default AddSnapshot;
