import React from "react";
import {useState} from "react";
import { Box, Center, Container, Button, Image, Text } from "@chakra-ui/react"
function AddSnapshot(props) {

    const [image, setImage] = useState(null)

     pasteFromClipboard = (e) => {
      if(e.clipboardData.files.length > 0){
        if(e.clipboardData.files[0].type.startsWith("image/")){
          setSnapshotImage(e.clipboardData.files[0]);
        }
      }
    }

    const setSnapshotImage = (file) => {
      const fileReader = new FileReader();

      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        setImage(fileReader.result);
      }

    }
    return (
        <Box maxW="sm" borderWidth="1px" borderRadius="lg" overflow="hidden">
            <Container my={4}>
            <Center>
            <Image fallbackSrc="https://via.placeholder.com/400" onPaste={this.pasteFromClipboard.bind} src={image}/>
            </Center>
            <Text color="gray.500" fontSize="sm" mt={3n}>Ctrl + V to paste your image</Text>
    <Button
            mt={4}
            colorScheme="teal"
            isLoading={props.isSubmitting}
            type="submit"
          >
            Submit
          </Button>
    </Container>
        </Box>
)
}

export default AddSnapshot;
